frappe.ui.form.on('Bank Transaction Payments', {
    create_document: function(frm, cdt,cdn) {
    // console.log("HERE")
    // let D = frm.fields_dict
    let pay_row = locals[cdt][cdn]
    let BTdoc = frm.doc
    let prow_pay = pay_row.payment_entry
    let prow_pay_doc = pay_row.payment_document
    let alloc_amt = frm.fields_dict['withdrawal'].value+frm.fields_dict['deposit'].value

    // console.log(`Row Index: ${prow_pay.idx}`)
    // if(!(prow_pay)){
    //     prow_pay=""
    // }
    frappe.call({
        method: "cspl_accounting_tweaks.cspl_accounting_tweaks.utils.create_document.create_payment_entry_bank_transaction",
        args: {
                    "bank_transaction_name": BTdoc.name,
                    "payment_row_doc_type": prow_pay_doc,
                    "payment_entry": prow_pay
                },
        callback: function(r){
            if(!r.exc && r.message) {
                console.log(r)
                console.log(alloc_amt)
                console.log(r.message.name)
                frappe.model.set_value(cdt,cdn, 'allocated_amount',alloc_amt)
                frappe.model.set_value(cdt,cdn, 'payment_entry',r.message.name)
                frm.save
                frm.refresh
                frappe.set_route("Form", r.message.doctype, r.message.name);
            }
        }
    })

        }
    })
